function [path,linkNumbers] = FindPath(proposedBackupLinks,seed,flowsMatrix,source,target,visitedNodes)


% Random 
%rng(seed);
loopMax = 400;

% PATH
currentNode = source ;
path = source;
p = 1;
flows=flowsMatrix(:,1:2);

while 1
   for i=1:1:loopMax;
   random = randi(20) ;    
   for j=1:1:length(proposedBackupLinks)
       j = mod(random+j,length(proposedBackupLinks))+1;
       if (flows(proposedBackupLinks(j),1)==currentNode && ~ismember(flows(proposedBackupLinks(j),2),visitedNodes))
           if (flows(proposedBackupLinks(j),2)==target)
               linkNumbers(p) = proposedBackupLinks(j) ;
                 p=p+1;
                  path(p)=target;  
                  return
           end
        visitedNodes = union(visitedNodes,flows(proposedBackupLinks(j),2));
        currentNode = flows(proposedBackupLinks(j),2) ;
        linkNumbers(p) = proposedBackupLinks(j) ;
                 p=p+1;
                 path(p) = currentNode     ;
               length(linkNumbers);
        
                 
                 
                 break
       end
   end

for j=1:1:length(proposedBackupLinks)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                