                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if (flows(proposedBackupLinks(j),2)==target )
        visitedNodes = union(visitedNodes,flows(proposedBackupLinks(j),2));
        target = flows(proposedBackupLinks(j),1);
        linkNumbers(p) = proposedBackupLinks(j) ;
                  p=p+1;
                  path(p) = currentNode     ;
    end
end
                  
   [isMem,index] = ismember([currentNode,target], flows, 'rows') ;
   
   if (  isMem  ) 
       linkNumbers(p) = index ;
       p=p+1;
       path(p)=target;         
       return
   end      
       
  
   linkNumber = randi(length(flowsMatrix),1);     
             if (flowsMatrix(linkNumber,1)==currentNode && ~ismember(flowsMatrix(linkNumber,2),visitedNodes))
                 visitedNodes = union(visitedNodes,flowsMatrix(linkNumber,2));
                currentNode = flowsMatrix(linkNumber,2) ;
                 linkNumbers(p) = linkNumber ;
                 p=p+1;
                 path(p) = currentNode ;
             end
                    
             
  
      
   end
   

   
   if currentNode == target
       return
   end
   
   if p>1
       p = p-1 ;
   else
      disp('ERROR - CANNOT FIND ANY PATH FOR PROVIDED DATA')
      path=0;
      return ;
   end
   
     currentNode = path(p) ;
     
   
   
   
   
end
end